package validator

import (
	"runtime"
	"testing"

	"github.com/stretchr/testify/suite"
)

type ValidatorSuite struct {
	suite.Suite
	v *V
}

func (s *ValidatorSuite) SetupTest() {
	s.T().Helper()

	// copy of internal/testenv.MustHaveExternalNetwork()
	// because use of internal/ packages is prohibited,
	// but Validator tests require external network
	if runtime.GOOS == "js" {
		s.T().Skipf("skipping test: no external network on %s", runtime.GOOS)
	}
	if testing.Short() {
		s.T().Skipf("skipping test: no external network in -short mode")
	}

	s.v = New([]string{"spam.host"}, []string{"spam@email.host"}, []string{"spam"}, "TRACE").(*V)
}

func (s *ValidatorSuite) TestNew() {
	validator := New([]string{}, []string{}, []string{}, "TRACE")

	s.IsType(&V{}, validator)
}

func (s *ValidatorSuite) TestA() {
	tests := map[string]bool{
		"":              false,
		"example.com":   true,
		"doesnt.exists": false,
	}

	for host, expected := range tests {
		s.Run(host, func() {
			result := s.v.A(host)

			s.Equal(expected, result)
		})
	}
}

