func (s *ValidatorSuite) TestDomain() {
	tests := map[string][]bool{
		"":                {true, false},
		"n.e":             {true, false},
		"example.com":     {true, true},
		"that's invalid!": {true, false},
		"nnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnnn.e": {true, false},
	}

	for host, slice := range tests {
		s.Run(host, func() {
			result := s.v.Domain(host, slice[0])

			s.Equal(slice[1], result)
		})
	}
}

// TestDomain2 - additional test cases with the same values
func (s *ValidatorSuite) TestDomain2() {
	tests := map[string][]bool{
		"": {false, true},
	}

	for host, slice := range tests {
		s.Run(host, func() {
			result := s.v.Domain(host, slice[0])

			s.Equal(slice[1], result)
		})
	}
}

func (s *ValidatorSuite) TestEmail() {
	tests := map[string]bool{
		"":                                 true,
		"a@e":                              false,
		"example.com":                      false,
		"doesnt.exists":                    false,
		"that's invalid!":                  false,
		"example@example.com":              false,
		"invalid@iana.org":                 false,
		"iana@iana.org":                    true,
		"CYKPDhCreCEGff@dkimvalidator.com": true,
		"spam@validhost.com":               false,
		"notspam@spam.host":                false,
		"notspam@sub.spam.host":            false,
		"spam@email.host":                  false,
		".e": false,
	}

	for host, expected := range tests {
		s.Run(host, func() {
			result := s.v.Email(host)

			s.Equal(expected, result)
		})
	}
}

func (s *ValidatorSuite) TestGetBase() {
	tests := map[string]string{
		"example.com":             "example.com",
		"sub.example.com":         "example.com",
		"sub.sub.sub.example.com": "example.com",
		"example.etke.host":       "example.etke.host",
		"sub.example.etke.host":   "example.etke.host",
		"example.co.uk":           "example.co.uk",
		"sub.example.co.uk":       "example.co.uk",
		"example.gov.la":          "example.gov.la",
		"sub.example.gov.la":      "example.gov.la",
		"example.com.sg":          "example.com.sg",
		"sub.example.com.sg":      "example.com.sg",
	}

	for host, expected := range tests {
		s.Run(host, func() {
			result := s.v.GetBase(host)

			s.Equal(expected, result)
		})
	}
}

func TestValidatorSuite(t *testing.T) {
	suite.Run(t, new(ValidatorSuite))
}
